class Solution {
    private class Node{
        int receiver;
        int time;
        Node(int i, int j){
            receiver = i;
            time = j;
        }
    }
    public int networkDelayTime(int[][] times, int N, int K) {
        List<List<Node>> graph = new ArrayList<List<Node>>();

        //generate the graph logging the travel from each node to other nodes and its corresponding time needed
        for(int i = 0; i < N + 1; i++) graph.add(new ArrayList<Node>());
        for(int i = 0; i < times.length; i++){
            int[] travel = times[i];
            graph.get(travel[0]).add(new Node(travel[1], travel[2]));
        }
        
        //initialize the time to light
        int[] lights = new int[N + 1];
        for(int i = 0; i < lights.length; i++){
            if(i == K) lights[i] = 0;
            else lights[i] = 6001;
        }
        
        //start lighting
        int result = 0;
        Set<Integer> lighten = new HashSet<Integer>();
        Set<Integer> lighting = new HashSet<Integer>();
        lighting.add(K);
        while(lighting.size() != 0){
            for(int i = 0; i < lights.length; i++){
                if(lights[i] == 0 && !lighten.contains(i)){
                    updateTime(graph, i, lights, lighten, lighting);
                    if(lighten.size() == N) return result;
                }
            }
            for(int i = 0; i < lights.length; i++){
                if(lights[i] > 0 && lights[i] < 6001) lights[i]--;
            }
            result++;
        }
        return -1;
    }
    private void updateTime(List<List<Node>> graph, int node, int[] lights, Set<Integer> lighten, Set<Integer> lighting){
        lighten.add(node);
        if(lighting.contains(node)) lighting.remove(node);
        for(Node travel : graph.get(node)){
            if(!lighten.contains(travel.receiver)){
                if(travel.time == 0) updateTime(graph, travel.receiver, lights, lighten, lighting);
                else lighting.add(travel.receiver);

                if(lights[travel.receiver] > travel.time) lights[travel.receiver] = travel.time;
            }
        }   
        return;
    }

}
